
// Data structure untuk seluruh form
const formFields = {
    header: {
        title: "BERITA ACARA SERAH TERIMA",
        subtitle: "MEKANIKAL/ ELEKTRIKAL"
    },
    mainInfo: {
        ownerName: { label: "Nama Pemilik", id: "ownerName" },
        storeName: { label: "Nama Toko", id: "storeName" },
        kavling: { label: "Kavling", id: "kavling" },
        date: { label: "Pada hari ini Tanggal", id: "date" }
    },
    kwhMeter: {
        title: "KWH Meter",
        fields: {
            regNo: { label: "No Registrasi", id: "kwhReg" },
            kva: { label: "KVA Terpasang", id: "kwhKva" },
            amp: { label: "Ampere Meter", id: "kwhAmp" },
            phase: { label: "Phase", id: "kwhPhase" },
            ct: { label: "Current Transformer", id: "currentTransformer" },
            reading: { label: "Angka Stand Awal", id: "kwhReading" }
        }
    },
    waterMeter: {
        title: "Meter Air",
        fields: {
            reading: { label: "Angka Stand Awal", id: "waterReading" },
            regNo: { label: "No Registrasi", id: "waterReg" }
        }
    },
    phone: {
        title: "Telepon",
        fields: {
            lines: { label: "Jumlah Line", id: "phoneCount" },
            number: { label: "Nomor Telepon", id: "phoneNumber" }
        }
    },
    signatures: {
        management: { 
            title: "Pihak I: Manajemen BTC Fashion Mall",
            id: "managementName"
        },
        tenant: {
            title: "Pihak II: Penyewa/Wakil",
            id: "tenantName"
        }
    }
};

function getFieldValue(id) {
    return document.getElementById(id).value;
}

function addTextToPDF(doc, text, x, y, options = {}) {
    doc.text(text, x, y, options);
}
    function createPDFHeader(doc) {
        // Add a subtle header background
        doc.setFillColor(240, 240, 240);
        doc.rect(0, 0, 210, 30, 'F');
    
        // Modern typography
        doc.setFont("helvetica", "bold");
        doc.setFontSize(20);
        addTextToPDF(doc, formFields.header.title, 105, 15, { align: "center" });
        doc.setFontSize(14);
        doc.setFont("helvetica", "normal");
        addTextToPDF(doc, formFields.header.subtitle, 105, 25, { align: "center" });
    }    
    // Add icon helper function
    function addHeaderIcon(doc, iconBase64, x, y, size = 8) {
        try {
            doc.addImage(iconBase64, 'PNG', x, y - 4, size, size);
        } catch (e) {
            console.log('Icon could not be loaded');
        }
    }

    // Define icons as base64 strings
    const icons = {
        
        kwh: 'data:image/png;base64,',  // Add your electricity icon base64
        water: 'data:image/png;base64,', // Add your water icon base64
        phone: 'data:image/png;base64,'  // Add your phone icon base64
    };

    function drawContainer(doc, startY, height, title = '', icon = null) {
        doc.setDrawColor(200, 200, 200);
        doc.setFillColor(252, 252, 252);
        doc.roundedRect(5, startY - 5, 200, height, 3, 3, 'FD');
    
        if (title) {
            doc.setFillColor(240, 240, 240);
            doc.roundedRect(5, startY - 5, 200, 10, 3, 3, 'FD');
        
            if (icon) {
                addHeaderIcon(doc, icon, 10, startY);
                doc.setFont("helvetica", "bold");
                addTextToPDF(doc, title, 22, startY); // Adjusted x position to accommodate icon
            } else {
                doc.setFont("helvetica", "bold");
                addTextToPDF(doc, title, 10, startY);
            }
        }
    }

    function addMainInfo(doc, startY = 40) {
        const containerHeight = 60;
        drawContainer(doc, startY, containerHeight, 'INFORMASI UTAMA');
    
        doc.setFontSize(11);
        let yPosition = startY + 10;

        // Owner name in full width
        doc.setFont("helvetica", "normal");
        addTextToPDF(doc, `${formFields.mainInfo.ownerName.label}:`, 10, yPosition);
        doc.setFont("helvetica", "bold");
        addTextToPDF(doc, getFieldValue(formFields.mainInfo.ownerName.id), 70, yPosition);
        yPosition += 10;

        // Store name and kavling in two columns
        doc.setFont("helvetica", "normal");
        addTextToPDF(doc, `${formFields.mainInfo.storeName.label}:`, 10, yPosition);
        doc.setFont("helvetica", "bold");
        addTextToPDF(doc, getFieldValue(formFields.mainInfo.storeName.id), 70, yPosition);

        doc.setFont("helvetica", "normal");
        addTextToPDF(doc, `${formFields.mainInfo.kavling.label}:`, 110, yPosition);
        doc.setFont("helvetica", "bold");
        addTextToPDF(doc, getFieldValue(formFields.mainInfo.kavling.id), 150, yPosition);
        yPosition += 10;

        // Date in full width
        doc.setFont("helvetica", "normal");
        addTextToPDF(doc, `${formFields.mainInfo.date.label}:`, 10, yPosition);
        doc.setFont("helvetica", "bold");
        addTextToPDF(doc, getFieldValue(formFields.mainInfo.date.id), 70, yPosition);
        yPosition += 10;

        // Additional text
        doc.setFont("helvetica", "normal");
        doc.setFontSize(11);
        addTextToPDF(doc, "Pihak penyewa / wakil penyewa dan pihak Manajemen d'botanica Mall bersama-sama", 10, yPosition);
        yPosition += 5;
        addTextToPDF(doc, "telah menyaksikan pencatatan fasilitas-fasilitas berikut :", 10, yPosition);

        return startY + containerHeight + 8;
    }

    function addKWHMeter(doc, startY) {
        const containerHeight = 45;
        drawContainer(doc, startY, containerHeight, formFields.kwhMeter.title, icons.kwh);

        let yPosition = startY + 10;
        doc.setFontSize(11);

        const fields = Object.values(formFields.kwhMeter.fields);
        const leftX = 15;
        const rightX = 110;

        fields.forEach((field, index) => {
            const x = index % 2 === 0 ? leftX : rightX;
            const y = yPosition + Math.floor(index / 2) * 10;
    
            doc.setFont("helvetica", "normal");
            addTextToPDF(doc, `${field.label}:`, x, y);
            doc.setFont("helvetica", "bold");
            addTextToPDF(doc, getFieldValue(field.id), x + 65, y);
        });

        return startY + containerHeight + 8;
    }

    function addWaterMeterAndPhone(doc, startY) {
        const containerHeight = 50;
        const halfWidth = 95;

        // Water Meter Container (Left)
        doc.setDrawColor(200, 200, 200);
        doc.setFillColor(252, 252, 252);
        doc.roundedRect(5, startY - 5, halfWidth, containerHeight, 3, 3, 'FD');

        // Title bar for Water Meter
        doc.setFillColor(240, 240, 240);
        doc.roundedRect(5, startY - 5, halfWidth, 10, 3, 3, 'FD');
        addHeaderIcon(doc, icons.water, 10, startY);
        doc.setFont("helvetica", "bold");
        addTextToPDF(doc, formFields.waterMeter.title, 22, startY);

        // Water Meter Content
        let yPosition = startY + 10;
        doc.setFontSize(11);
        Object.values(formFields.waterMeter.fields).forEach(field => {
            doc.setFont("helvetica", "normal");
            addTextToPDF(doc, `${field.label}:`, 10, yPosition);
            doc.setFont("helvetica", "bold");
            addTextToPDF(doc, getFieldValue(field.id), 65, yPosition);
            yPosition += 10;
        });

        // Phone Container (Right)
        doc.setDrawColor(200, 200, 200);
        doc.setFillColor(252, 252, 252);
        doc.roundedRect(110, startY - 5, halfWidth, containerHeight, 3, 3, 'FD');

        // Title bar for Phone
        doc.setFillColor(240, 240, 240);
        doc.roundedRect(110, startY - 5, halfWidth, 10, 3, 3, 'FD');
        addHeaderIcon(doc, icons.phone, 115, startY);
        doc.setFont("helvetica", "bold");
        addTextToPDF(doc, formFields.phone.title, 127, startY);

        // Phone Content
        yPosition = startY + 10;
        Object.values(formFields.phone.fields).forEach(field => {
            doc.setFont("helvetica", "normal");
            addTextToPDF(doc, `${field.label}:`, 115, yPosition);
            doc.setFont("helvetica", "bold");
            addTextToPDF(doc, getFieldValue(field.id), 170, yPosition);
            yPosition += 10;
        });

        return startY + containerHeight + 10;
    }

    function addSignatures(doc, startY = 230) {
    const { management, tenant } = formFields.signatures;
    
    addTextToPDF(doc, management.title, 10, startY);
    addTextToPDF(doc, "_______________________", 10, startY + 30);
    addTextToPDF(doc, `Nama: ${getFieldValue(management.id)}`, 10, startY + 40);
    
    addTextToPDF(doc, tenant.title, 120, startY);
    addTextToPDF(doc, "_______________________", 120, startY + 10);
    addTextToPDF(doc, `Nama    : ${getFieldValue(tenant.id)}`, 120, startY + 20);
    addTextToPDF(doc, "Jabatan :", 120, startY + 30);
}

document.addEventListener('DOMContentLoaded', function() {
    const sections = [
        { check: 'kwhCheck', content: 'kwhContent' },
        { check: 'waterCheck', content: 'waterContent' },
        { check: 'phoneCheck', content: 'phoneContent' }
    ];

    sections.forEach(section => {
        const checkbox = document.getElementById(section.check);
        const content = document.getElementById(section.content);
        
        checkbox.addEventListener('change', function() {
            content.classList.toggle('active', this.checked);
            const inputs = content.querySelectorAll('input');
            inputs.forEach(input => {
                input.disabled = !this.checked;
                input.required = this.checked;
            });
        });
    });

    // Add CT field handling
    const ctRadios = document.querySelectorAll('input[name="ctStatus"]');
    const ctField = document.getElementById('currentTransformer');

    ctRadios.forEach(radio => {
        radio.addEventListener('change', function() {
            ctField.disabled = (this.value === 'tidak');
            if (this.value === 'tidak') {
                ctField.value = '';
            }
        });
    });
});

function validateCT() {
    const ctStatus = document.querySelector('input[name="ctStatus"]:checked');
    const ctField = document.getElementById('currentTransformer');

    if (ctStatus && ctStatus.value === 'ada' && !ctField.value.trim()) {
        alert('CT harus diisi!');
        ctField.focus();
        return false;
    }
    return true;
}

async function generatePDF() {
    const ctStatus = document.querySelector('input[name="ctStatus"]:checked');
    
    if (!ctStatus) {
        alert('Silakan pilih status Current Transformer (Ada/Tidak)');
        return;
    }

    if (!validateCT()) {
        return;
    }

    const { jsPDF } = window.jspdf;
    const doc = new jsPDF();

    createPDFHeader(doc);
    let nextY = addMainInfo(doc);
    nextY = addKWHMeter(doc, nextY);
    nextY = addWaterMeterAndPhone(doc, nextY); // Replace separate calls with this
    addSignatures(doc, nextY);

    doc.save("Berita_Acara_Serah_Terima.pdf");
}

function clearForm() {
    // Reset main form
    document.getElementById('serahTerimaForm').reset();
    
    // Reset radio buttons for Current Transformer
    document.getElementById('ctYes').checked = false;
    document.getElementById('ctNo').checked = false;
    
    // Reset Current Transformer text input
    document.getElementById('currentTransformer').value = '';
    document.getElementById('currentTransformer').disabled = true;
    
    // Reset all section toggles (checkboxes)
    document.getElementById('kwhCheck').checked = false;
    document.getElementById('waterCheck').checked = false;
    document.getElementById('phoneCheck').checked = false;
    
    
}

function submitForm() {
    const formData = new FormData(document.getElementById('serahTerimaForm'));

    fetch('https://script.google.com/macros/s/AKfycbwg-iSwM4CVPisGbSLhR613bpTtBrQq5EQVtoLYesmpkBrvRjdhC3DHt9rP-9RmXw2f/exec', {
        method: 'POST',
        body: formData
    })
    .then(response => {
        if (response.ok) {
            alert('Form submitted successfully!');
        } else {
            alert('Form submission failed.');
        }
    })
    .catch(error => {
        console.error('Error:', error);
        alert('There was an error submitting the form.');
    });
}